import React from "react";
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import Login from "./components/login/login"; 
import UserInfo from "./components/UserInfo";
import Dashboard from "./components/dashboard"; 
import ChatSystem from "./components/chatsystem/script";
import MarksChart from "./components/ProgressReport/report";
import Achievements from "./components/achievements/achievements";
import HomePage from "./components/homepage/homepage";
import { useAuth } from './components/AuthContext';

function App() {
  const { token, setToken, email, setEmail, photoData, setPhoto } = useAuth();

  const updateToken = (newToken) => {
    setToken(newToken);
    localStorage.setItem('userToken', newToken);
  }

  const updateEmail = (newEmail) => {
    setEmail(newEmail);
    localStorage.setItem('userEmail', newEmail);
  };

  const updatePhoto = (newPhoto) => { // Add this function to update photoData
    setPhoto(newPhoto);
    localStorage.setItem('userPhoto', newPhoto);
  }





  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Define the route for the homepage */}
          <Route path="/" element={<HomePage />} />

          {/* Define routes for authenticated content */}
          <Route
            path="/login"
            element={<Login updateToken={updateToken} updateEmail={updateEmail} updatePhoto={updatePhoto} />}
          />
          <Route
            path="/user"
            element={<UserInfo token={token} email={email} photoData={photoData} />}
          />
          <Route
            path="/marks"
            element={<MarksChart token={token} />}
          />
          <Route
            path="/dashboard/*"
            element={
              token ? (
                <Dashboard>
                  <Link to="/dashboard/ChatSystem">Chat System</Link>
                  <Link to="/dashboard/achievements">View Achievements</Link>
                  <Link to="/dashboard/progressreport">View Progress Report</Link>
                </Dashboard>
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route
            path="/dashboard/ChatSystem"
            element={<ChatSystem />}
          />
          <Route
            path="/dashboard/achievements"
            element={<Achievements />}
          />
          <Route
            path="/dashboard/progressreport"
            element={<MarksChart token={token} />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;














