import React, { useState,useEffect} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link, Route, Routes } from "react-router-dom";
import "./dashboard.css";
import AchievementDropdown from "./achievements/achievements" 
import LogoutButton from "./logout/logout";

const Dashboard = () => {
      const [userInfo, setUserInfo] = useState(null);


  useEffect(() => {

    console.log("Before API request");
    // Make an API request to fetch user data
    fetch("/api/info") 
    .then((response) =>
    {  console.log("API Response:", response);
      
     return response.json()
  })
    .then((data) =>
    {
      console.log("API Response:", data);
       setUserInfo(data)
    })
    .catch((error) => console.error("Error fetching user data: ", error));
}, []);
   

 

  return (
    <div className="container-fluid">
      <div className="row">
        <nav id="sidebar" className="col-md-3 col-lg-2 d-md-block bg-light sidebar">
          <div className="position-sticky">
            <ul className="nav flex-column">
              <li className="nav-item">
                <Link to="/dashboard/ChatSystem" className="nav-link">
                  Chat System
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/dashboard/achievements" className="nav-link">
                  Track Achievements
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/dashboard/progressreport" className="nav-link">
                  View Progress Report
                </Link>
              </li>
            </ul>
          </div>
        </nav>

        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
            <h1 className="h2">Dashboard</h1>
            <LogoutButton />
          </div>
             
             {/* Display user data */}
        {userInfo ? (
          <div>
            <img src={userInfo.photo} alt="User" />
            <p>Name: {userInfo.name}</p>
            <p>Email: {userInfo.email}</p>
            <p>Student ID: {userInfo.studentID}</p>
          </div>
        ) : (
          <p>Loading user data...</p>
        )}
             

          <Routes>
            <Route path="/dashboard/achievements" element={<AchievementDropdown />} />
           
          </Routes>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
