import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { redirect } from 'react-router-dom';


const ProgressGraph = () => {
  const [marks, setMarks] = useState([]);

  useEffect(() => {
    let userToken = localStorage.getItem('userToken');

    if (!userToken) {
      redirect ( )
      return;
    }


    // Fetch marks data from the API
    axios.get('http://localhost:9002/progress', {
      headers: {
        Authorization: `Bearer ${userToken}` // Include the user's token in the headers
      }
    }).then((response) => {
      setMarks(response.data);
    });
  }, []);


   // Prepare data for midterm and final term charts

   const midtermData = (marks ?? []).map((progress) => ({
    name: 'Student ' + progress.studentId,
    midterm: parseFloat(progress.midterm),
  }));
  

  const finaltermData = (marks ?? []).map((progress) => ({
    name: 'Student ' + progress.studentId,
    midterm: parseFloat(progress.finalterm),
  }));
  

  return (
    <div>
      <h2>Student Marks</h2>
      <div style={{ width: '80%', height: 400, margin: 'auto' }}>
        <ResponsiveContainer width ="100%" height={300}>
          <BarChart
          data={midtermData} 
            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="midterm" fill="rgba(75, 192, 192, 0.2)" />
          </BarChart>
        </ResponsiveContainer>
        <ResponsiveContainer>
        <BarChart
            width={500}
            height={300}
            data={finaltermData} 
            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="finalterm" fill="rgba(75, 192, 192, 0.2)" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default ProgressGraph;
