import React, { useState } from 'react';
import axios from 'axios';
import './achievements.css';

function Achievements() {
  const [selectedOption, setSelectedOption] = useState('');
  const [optionInfo, setOptionInfo] = useState({});
  const [error, setError] = useState(null);

  const handleOptionSelect = async (option) => {
    setSelectedOption(option);

    try {
      const response = await axios.get(`http://localhost:9002/achievements?option=${option}`);

      if (response.status === 200) {
        setOptionInfo(response.data);
        console.log
        setError(null);
      } else {
        setError('Failed to fetch option information');
      }
    } catch (error) {
      console.error('API request error:', error);
      setError('Internal server error');
    }
  };

  return (
    <div>
      <h2>Achievements</h2>
      <div>
        <select
          value={selectedOption}
          onChange={(e) => handleOptionSelect(e.target.value)}
        >
          <option value="">Select an option</option>
          <option value="Internship">Internship</option>
          <option value="Scholarship">Scholarship</option>
          <option value="Workshop">Workshop</option>
        </select>
      </div>
      {error && <p>Error: {error}</p>}
      {selectedOption && (
        <div>
          <h3>{selectedOption} Information</h3>
          <ul>
            {Object.entries(optionInfo).map(([key, value]) => (
              <li key={key}>
                <strong>{key}:</strong> {value}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Achievements;




