import React,{useState} from "react";
import { BrowserRouter as Router, Route, Routes, Link, Navigate} from 'react-router-dom';
import Login from "./components/login/login"; 
import UserInfo from "./components/UserInfo";
import Dashboard from "./components/dashboard"; 
import ChatSystem from "./components/chatsystem/script"
import MarksChart from "./components/ProgressReport/report";



function App(){
  const[token, setToken]= useState(localStorage.getItem('userToken'));

  const updateToken = (newToken) =>{
    setToken(newToken);
    localStorage.setItem('userToken',newToken);
  }




return (
  <Router>
    <div className="App">
      <Routes>

        <Route path="/dashboard">
            {token ? (
              <Dashboard>
                <Link to="/chatsystem">Chatsystem</Link>
                <Link to="/achievements">View Achievements</Link>
                <Link to="/progressreport">View Progress Report</Link>
              </Dashboard>
            ) : (
              <Navigate to="/login" />
            )}
         </Route>
          <Route path="/chatsystem" element={<ChatSystem />} />
          <Route path="/achievements" element={<div>View Achievements</div>} />
          <Route path="/progressreport" element={<MarksChart token={token} />} />
          <Route path="/" element={<Navigate to="/dashboard" />} />

      </Routes>


    </div>
  </Router>
)
}


export default App;
