import React, { useState } from "react";
import "./login.css"; // Make sure the import path matches your CSS file's location
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Login = ({ updateToken , updateEmail }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  // Use the useNavigate hook to handle navigation
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:9002/auth/login", {
        email,
        password,
      });

      if (response.status === 200) {
        const data = response.data;
         
        localStorage.setItem('userToken', data.token);
        localStorage.setItem('userEmail', data.user.email);

         console.log(email)

        updateToken(data.token);
        updateEmail(data.user.email)

        // Redirect to the dashboard after successful login
        navigate("/dashboard");
      } else {
        // Handle login error
        console.error('Login failed');
      }
    } catch (error) {
      console.error('Login error:', error);
    }
  };

  return (
    <div className="login-main">
      <div className="login">
        <h1>Login</h1>
        <form onSubmit={handleLogin}>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your password"
          />
          
          <button className="login-button" type="submit">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
