import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
// import { redirect } from 'react-router-dom';


const MarksChart = ({ token }) => {
  const [marksData, setMarksData] = useState(null);

  useEffect(() => { 
    
    // Fetch marks data from the API
    axios.get('http://localhost:9002/auth/user', {
      headers: {
        Authorization: `Bearer ${token} `

         // Include the user's token in the headers
      }
    }).then((response) => {
      setMarksData(response.data);
    })
    .catch((error) => {
      console.error('Fetch marks data error:', error);
    });
  }, [token]);

    const fetchMarksData = async () => {
      try {
        const response = await fetch('/marks', {
          // headers: {
          //   Authorization: `Bearer ${token}`,
          // },
        });

        if (response.ok) {
          const data = await response.json();
          setMarksData(data);
        } else {
          // Handle unauthorized or other errors
          console.error('Failed to fetch marks data');
        }
      } catch (error) {
        console.error('Fetch marks data error:', error);
      }
    };

    if (token) {
      fetchMarksData();
    }
  

   
  return (
    <div>
      <h2>Marks Chart</h2>
      {marksData !== null ? (
      <div style={{ width: '80%', height: 400, margin: 'auto' }}>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart
            data={marksData}
            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="studentId" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="midterm" fill="rgba(75, 192, 192, 0.2)" />
            <Bar dataKey="finalterm" fill="rgba(75, 192, 192,0.5)"/>
            </BarChart>
            </ResponsiveContainer>
            </div>
      ):(
        <p>Loading....</p>
      )}
            </div>
  );
      }



  

export default MarksChart;
