import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { CategoryScale } from 'chart.js';

const ProgressGraph = () => {
  const [progress, setMarks] = useState([]);

  useEffect(() => {
    // Fetch marks data from the API
    axios.get('/progress').then((response) => {
      setMarks(response.data);
    });
  }, []);

  const midtermData = progress.map((progress) => progress.midterm);
  const finaltermData = progress.map((progress) => progress.finalterm);

  const data = {
    labels: ['Student 1', 'Student 2'],
    datasets: [
      {
        label: 'Midterm Marks',
        data: midtermData,
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
      {
        label: 'Final Term Marks',
        data: finaltermData,
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 1,
      },
    ],
  };

  const options = {
    scales: {
      xAxes: [
        {
          type: 'category',
          labels: ['Student 1', 'Student 2'],
        },
      ],
      yAxes:[
        {
        type: 'category',
        
        }
      ],
    },
  };

  return (
    <div>
      <h2>Student Marks</h2>
      <div style={{ width: '80%', margin: 'auto' }}>
        <Bar data={data} options={options} />
      </div>
    </div>
  );
};

export default ProgressGraph;
