import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar, CategoryScale } from 'react-chartjs-2';

const ProgressGraph = () => {
  const [marks, setMarks] = useState([]);

  useEffect(() => {
    // Fetch marks data from the API
    axios.get('/marks').then((response) => {
      setMarks(response.data);
    });
  }, []);

  const midtermData = marks.map((mark) => mark.midterm);
  const finaltermData = marks.map((mark) => mark.finalterm);

  const data = {
    labels: ['Student 1','Student 2' ],
    datasets: [
      {
        label: 'Midterm Marks',
        data: midtermData,
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
      {
        label: 'Final Term Marks',
        data: finaltermData,
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 1,
      },
    ],
  };
  const options = {
    cascales: {
      x: {
        type: 'category',
        labels: ['Student 1', 'Student 2'],
      },
    },
  };
  

  return (
    <div>
      <h2>Student Marks</h2>
      <div style={{ width: '80%', margin: 'auto' }}>
        <Bar data={data} />
      </div>
    </div>
  );
};

export default ProgressGraph;
