import React, { useState } from "react";
import "./login.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Login = ({ updateToken, updateEmail, updatePhoto }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [selectedRole, setSelectedRole] = useState("student"); // Default to "student"

  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:9002/auth/login", {
        email,
        password,
        role: selectedRole, // Send the selected role to the server
      });

      if (response.status === 200) {
        const data = response.data;

        localStorage.setItem("userToken", data.token);
        localStorage.setItem("userEmail", data.user.email);
        localStorage.setItem("userPhoto", data.user.photo || "");

        updateToken(data.token);
        updateEmail(data.user.email);
        updatePhoto(data.user.photo || "");

        navigate("/dashboard");
      } else {
        console.error("Login failed");
      }
    } catch (error) {
      console.error("Login error:", error);
    }
  };

  const handleRoleChange = (e) => {
    setSelectedRole(e.target.value);

    // Change the background image based on the selected role
    const loginMain = document.querySelector(".login-main");
    if (e.target.value === "admin") {
      loginMain.style.backgroundImage = 'url("admin.jpg")';
    } else {
      loginMain.style.backgroundImage = 'url("Login.jpg")';
    }
  };

  return (
    <div className="login-main">
      <div className="login">
        <h1>Login</h1>
        <form onSubmit={handleLogin}>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your password"
          />
          <div>
            <label htmlFor="role">Select your role:</label>
            <select
              id="role"
              value={selectedRole}
              onChange={handleRoleChange}
            >
              <option value="student">Student</option>
              <option value="admin">Admin</option>
            </select>
          </div>
          <button className="login-button" type="submit">
            Login
          </button>
        </form>
      </div>
    </div>
  );
};
  

export default Login;


