import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const ProgressGraph = () => {
  const [marks, setMarks] = useState([]);

  useEffect(() => {

    // Fetch marks data from the API
    axios.get('./p').then((response) => {
      setMarks(response.data);
    });
  }, []);

  const midtermData = marks.map((progress) => ({
    name: 'Student ' + progress.studentId,
    midterm: progress.midterm,
  }));

  const finaltermData = marks.map((progress) => ({
    name: 'Student ' + progress.studentId,
    finalterm: progress.finalterm,
  }));

  return (
    <div>
      <h2>Student Marks</h2>
      <div style={{ width: '80%', height: 400, margin: 'auto' }}>
        <ResponsiveContainer>
          <BarChart
            width={500}
            height={300}
            data={midtermData} // Use either midtermData or finaltermData as your dataset
            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="midterm" fill="rgba(75, 192, 192, 0.2)" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default ProgressGraph;
