import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link, Routes, useNavigate } from "react-router-dom";
import LogoutButton from "./logout/logout";
import { useAuth } from "./AuthContext";

const AdminDashboard = () => {
  const { token, email } = useAuth();
  const [adminInfo, setAdminInfo] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedPhoto, setSelectedPhoto] = useState(null);
  const [uploadingPhoto, setUploadingPhoto] = useState(false);

  const navigate = useNavigate();

  const { isAdmin } = useAuth();

 

  useEffect(() => {
    const fetchAdminInfo = async () => {
      try {
        if (token && isAdmin && email) {
          const response = await fetch(`http://localhost:9002/data?email=${email}`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });

          if (response.status === 200) {
            const adminData = await response.json();
            setAdminInfo(adminData);
            setSelectedPhoto(adminData.photo);
          }

          setIsLoading(false);
        } else {
          console.error('Failed to fetch admin information');
        }
      } catch (error) {
        console.error('Fetch admin info error:', error);
      }
    };

    if (token && isAdmin && email) {
      fetchAdminInfo();
    }
  }, [token, isAdmin, email, navigate]);

  const handlePhotoUpload = async (e) => {
    e.preventDefault();
    console.log('Uploading photo...');


    const formData = new FormData();
    formData.append('photo', e.target.files[0]);
   

    setUploadingPhoto(true);

    try {
      const response = await fetch(`http://localhost:9002/uploadAdminPhoto`, {
        method: "POST",
        body: formData,
      });

      if (response.status === 200) {
        console.log('Photo uploaded successfully');

        const adminData = await response.json();
        setSelectedPhoto(adminData.photo);
      } else {
        console.error("Photo upload failed", err);
      }
    } catch (error) {
      console.error("Photo upload error:", error);
    } finally {
      setUploadingPhoto(false);
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <nav id="sidebar" className="col-md-3 col-lg-2 d-md-block bg-light sidebar">
          <div className="position-sticky">
            <ul className="nav flex-column">
              <li className="nav-item">
                <Link to="/admindashboard/ChatSystem" className="nav-link">
                  Chat System
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/admindashboard/form" className="nav-link">
                  Form
                </Link>
              </li>
            </ul>
          </div>
        </nav>

        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
            <h1 className="h2">Admin Dashboard</h1>
            <LogoutButton />
          </div>

          {isLoading ? (
            <p>Loading admin data...</p>
          ) : (
            <div>
             <div>
  {isAdmin && (
    <div>
      <input type="file" id="fileInput" onChange={handlePhotoUpload} />
      {uploadingPhoto && <p>Uploading photo...</p>}
      
    </div>
  )}
  {selectedPhoto !== null && (
    <div>
      <img
        src={`data:image/jpeg;base64,${selectedPhoto}`}
        
        style={{ maxWidth: '200px', borderRadius: '50%' }}
      />
    </div>
  )}
</div>

              <div className="mt-4">
                {adminInfo ? ( // Check if adminInfo is not null
                  <>
                    <p><strong>Name:</strong> {adminInfo.name}</p>
                    <p><strong>Email:</strong> {adminInfo.email}</p>
                    <p><strong>Admin Id:</strong> {adminInfo.adminId}</p>
                    <p><strong>Faculty:</strong> {adminInfo.faculty}</p>
                  </>
                ) : (
                  <p>Admin information not available.</p>
                )}
              </div>
            </div>
          )}

          <Routes>
            {/* Define your admin-specific routes here */}
          </Routes>
        </main>
      </div>
    </div>
  );
}

export default AdminDashboard;
