import React, { useState,use} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link, Route, Routes } from "react-router-dom";
import "./dashboard.css";
import AchievementDropdown from "./achievements/achievements" 
import LogoutButton from "./logout/logout";

const Dashboard = () => {

  const [userData, setUserData] = useState({
    photoUrl: "",
    name: "",
    email: "",
    studentId: "",
  });


  useEffect(() => {
    // Fetch user data from the server/database here
    // Update the userData state with the fetched data
    // Example:
    fetchUserDataFromServer().then((data) => {
      setUserData({
        photoUrl: data.photoUrl,
        name: data.name,
        email: data.email,
        studentId: data.studentId,
      });
    });
  }, []);

 // Function to simulate fetching user data from the server
 const fetchUserDataFromServer = () => {
  return new Promise((resolve) => {
    // Simulated data (replace with actual API call)
    const userData = {
      photoUrl: "user-photo-url.jpg",
      name: "John Doe",
      email: "john.doe@example.com",
      studentId: "12345",
    };
    setTimeout(() => {
      resolve(userData);
    }, 1000); // Simulated delay
  });
};

  return (
    <div className="container-fluid">
      <div className="row">
        <nav id="sidebar" className="col-md-3 col-lg-2 d-md-block bg-light sidebar">
          <div className="position-sticky">
            <ul className="nav flex-column">
              <li className="nav-item">
                <Link to="/dashboard/ChatSystem" className="nav-link">
                  Chat System
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/dashboard/achievements" className="nav-link">
                  Track Achievements
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/dashboard/progressreport" className="nav-link">
                  View Progress Report
                </Link>
              </li>
            </ul>
          </div>
        </nav>

        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
            <h1 className="h2">Dashboard</h1>
            <LogoutButton />
          </div>

 {/* Display user data */}
 <div className="user-profile">
            <img src={userData.photoUrl} alt="User" className="user-photo" />
            <div className="user-details">
              <p>Name: {userData.name}</p>
              <p>Email: {userData.email}</p>
              <p>Student ID: {userData.studentId}</p>
            </div>
          </div>


          <Routes>
            <Route path="/dashboard/achievements" element={<AchievementDropdown />} />
           
          </Routes>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
