import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link, Route, Routes, useNavigate } from "react-router-dom";
import "./dashboard.css";
import Achievements from "./achievements/achievements";
import LogoutButton from "./logout/logout";
import { useAuth } from "./AuthContext";

const Dashboard = () => {
  const { token, email } = useAuth();
  const [userInfo, setUserInfo] = useState(null);
  const [currentSemester, setCurrentSemester] = useState(null);
  const [profilePhoto, setProfilePhoto] = useState(null); // State for profile photo
  const [selectedPhoto, setSelectedPhoto] = useState(null);
  const [isEditingProfilePhoto, setIsEditingProfilePhoto] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Make an API request to fetch user data
    const fetchUserInfo = async () => {
      try {
        if (token && email) {
          const response = await fetch(`http://localhost:9002/info?email=${email}`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });
          if (response.status === 200) {
            const userData = await response.json();
            setUserInfo(userData);

            // Calculate current semester based on a fixed start date
            const startDate = new Date(userData.startDate);
            const currentDate = new Date();

            // Calculate the difference in months
            const diffMonths = (currentDate.getFullYear() - startDate.getFullYear()) * 12 + (currentDate.getMonth() - startDate.getMonth());
            const currentSemesterNumber = Math.floor(diffMonths / 5) + 1;
            setCurrentSemester(currentSemesterNumber);

            // Set profile photo URL
            setProfilePhoto(userData.photo);
          } else {
            console.error('Failed to fetch user information');
          }
        }
      } catch (error) {
        console.error('Fetch user info error:', error);
      }
    };

    if (token) {
      fetchUserInfo();
    }
  }, [token, email, navigate]);

  const formatStartDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US');
  };

  // Function to handle file input change for profile photo
  const handleProfilePhotoChange = (e) => {
    const selectedPhoto = e.target.files[0];

   
  };

  // Function to save the edited profile photo
  const saveProfilePhoto = async () => {
    try {
      // Create a form data to send the photo to the server
      const formData = new FormData();
      formData.append('photo', selectedPhoto);

      // Make an API request to save the photo
      const response = await fetch('http://localhost:9002/editProfilePhoto', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formData,
      });

      if (response.status === 200) {
        setIsEditingProfilePhoto(false);
      } else {
        console.error('Failed to save profile photo');
      }
    } catch (error) {
      console.error('Save profile photo error:', error);
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <nav id="sidebar" className="col-md-3 col-lg-2 d-md-block bg-light sidebar">
          <div className="position-sticky">
            <ul className="nav flex-column">
              <li className="nav-item">
                <Link to="/dashboard/ChatSystem" className="nav-link">
                  Chat System
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/dashboard/achievements" className="nav-link">
                  Track Achievements
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/dashboard/progressreport" className="nav-link">
                  View Progress Report
                </Link>
              </li>
            </ul>
          </div>
        </nav>

        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
            <h1 className="h2">Dashboard</h1>
            <LogoutButton />
          </div>

          {/* Display user data */}
          {userInfo ? (
            <div>
              <div>
                {isEditingProfilePhoto ? (
                  <div>
                    <img src={profilePhoto} alt="User" />
                    <input type="file" accept="image/*" onChange={handleProfilePhotoChange} />
                    <button onClick={saveProfilePhoto}>Save</button>
                  </div>
                ) : (
                  <div>
                    <img src={profilePhoto} alt="User" />
                    <button onClick={() => setIsEditingProfilePhoto(true)}>Edit Photo</button>
                  </div>
                )}
              </div>
              <p>Name: {userInfo.name}</p>
              <p>Email: {userInfo.email}</p>
              <p>Student Id: {userInfo.studentId}</p>
              {userInfo.startDate && (
                <p>Start Date: {formatStartDate(userInfo.startDate)}</p>
              )}
              {currentSemester !== null && (
                <p>Current Semester: {currentSemester}</p>
              )}
            </div>
          ) : (
            <p>Loading user data...</p>
          )}

          <Routes>
            <Route path="/dashboard/achievements" element={<Achievements />} />
          </Routes>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;





