import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const ProgressGraph = () => {
  const [marks, setMarks] = useState([]);

  useEffect(() => {

    // Fetch marks data from the API
    axios.get('http://localhost:9002/user/marks').then((response) => {
      setMarks(response.data);
    });
  }, []);

   // Prepare data for midterm and final term charts

   const midtermData = marks.midtermMarks.map((value, index) => ({
    name: `MidSem${index + 1}`,
    midterm: value,
  }));

  const finaltermData = marks.finaltermMarks.map((value, index) => ({
    name: `FinalSem${index + 1}`,
    finalterm: value,
  }));

  return (
    <div>
      <h2>Student Marks</h2>
      <div style={{ width: '80%', height: 400, margin: 'auto' }}>
        <ResponsiveContainer width ="100%" he>
          <BarChart
            width={500}
            height={300}
            data={midtermData} 
            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="midterm" fill="rgba(75, 192, 192, 0.2)" />
          </BarChart>
        </ResponsiveContainer>
        <ResponsiveContainer>
        <BarChart
            width={500}
            height={300}
            data={finaltermData} 
            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="finalterm" fill="rgba(75, 192, 192, 0.2)" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default ProgressGraph;
