import React, { useState } from "react";
import "./login.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Login = ({ updateToken }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [photo, setPhoto] = useState(null); // Add state to store the selected photo

  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Prepare the form data with email, password, and photo
      const formData = new FormData();
      formData.append("email", email);
      formData.append("password", password);
      if (photo) {
        formData.append("photo", photo);
      }

      const response = await axios.post("http://localhost:9002/auth/login", formData);

      if (response.status === 200) {
        const data = response.data;

        localStorage.setItem('userToken', data.token);
        localStorage.setItem('userEmail', data.user.email);

        updateToken(data.token);

        navigate("/dashboard");
      } else {
        console.error('Login failed');
      }
    } catch (error) {
      console.error('Login error:', error);
    }
  };

  // Function to handle file input change
  const handlePhotoChange = (e) => {
    const selectedPhoto = e.target.files[0];
    setPhoto(selectedPhoto);
  };

  return (
    <div className="login-main">
      <div className="login">
        <h1>Login</h1>
        <form onSubmit={handleLogin}>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your password"
          />

          {/* Add file input for photo upload */}
          <input
            type="file"
            accept="image/*"
            onChange={handlePhotoChange}
            placeholder="Upload your profile photo"
          />

          <button className="login-button" type="submit">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;

